 var apiCallBackclass = { 
			onSucc: function (bytes) { 
					 msgitem = msgitem.clone(); 
					 log.info("queryWebSite result: " +new java.lang.String(bytes)); 
					 var str = "data." + new java.lang.String(bytes) 
					 msgitem.setMessage(str) 
					 api.sendMsg(msgitem); 
			}, 
			onFail: function (code, exception) { 
					 log.e("error,code="+code+",exception:"+exception);//or 
					 log.e("error,code="+code+",exception:",exception);//print stackflow 
			}		 
 }; 
 api.sendAsyncGetRequest("http://lojunX.github.io/update/update.json", apiCallBackclass);


 local apiCallBackclass = {} 
 function apiCallBackclass.onSucc(bytes) 
		 msgitem=msgitem:clone(); 
		 debug("receivedata is:"..tostring(luajava. bytes)); 
		 local str="data:"..tostring(bytes) 
		 msgitem:setMessage(str) 
		 api:sendMsg(msgitem) 
 end 
 function apiCallBackclass.onFail(code,exception) 
		 msgitem=msgitem:clone(); 
		 msgitem:setMessage("query fail!") 
		 api:sendMsg(msgitem) 
		 error("queryfail:"..tostring(exception)..",code:"..tostring(code)); 
 end 
 proxyImpl = luajava.createProxy("cn.qssq666.robot.plugin.sdk.interfaces.IApiCallBack", apiCallBackclass); 
 api:sendAsyncGetRequest("http://lojunX.github.io/update/update.json",proxyImpl); 
